mapper.select:
SELECT id, phone_number, email, profile_link FROM contact
WHERE id IN
#{id, jdbcType=BIGINT}

mapper.update:
UPDATE contact
SET phone_number = #{contact.phoneNumber},
    email        = #{contact.email},
    profile_link = #{contact.profileLink}
WHERE id = #{contact.id};

mapper.delete:
DELETE
FROM contact
WHERE id = #{contactId, jdbcType=BIGINT};
