mapper.select:
SELECT id, medical_card_id, type_id, heaviness, appearance_dttm, recovery_dt FROM illness
WHERE id IN
#{id, jdbcType=BIGINT}

mapper.update:
UPDATE illness SET

    medical_card_id = #{medicalCardId}


    type_id = #{typeId},


    heaviness = #{heaviness},


    appearance_dttm = #{appearanceDttm},


    recovery_dt = #{recoveryDt}

WHERE id = #{id};

mapper.delete:
DELETE
FROM illness
WHERE id = #{illnessId, jdbcType=BIGINT};
