mapper.select:
SELECT id, last_name, first_name, birth_dt, age, sex, contact_id, medical_card_id FROM person_data
WHERE id IN
#{id, jdbcType=BIGINT}

mapper.update:
UPDATE person_data SET

    last_name = #{lastName}


    first_name = #{firstName},


    birth_dt = #{birthDt},


    age = #{age},


    sex = #{sex}


    contact_id = #{contactId}


    medical_card_id = #{medicalCardId}


    parent_id = #{parentId}

WHERE id = #{id};

mapper.delete:
DELETE
FROM person_data
WHERE id = #{personDataId, jdbcType=BIGINT};
